import React, { useState } from "react";

function InputField(props) {
  const [label, setLabel] = useState(props.label);
  const [error, setError] = useState(props.error);

  return (
    <div className="flex flex-col text-sm">
      <label className="mb-1">
        Password
      </label>
      <div className="relative flex items-center">
        <input type="text" className="w-full pr-8 rounded-md shadow-sm text-sm text-red-900 border-red-300 focus:ring-red-500 focus:border-red-500"/>
        <svg className="absolute right-0 h-5 w-5 mr-2 text-red-500" viewBox="0 0 20 20" fill="currentColor">
            <path fill-rule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clip-rule="evenodd"/>
        </svg>
      </div>
      <label className="mt-2 text-red-600">
        Your password must be more than 5 characters.
      </label>
    </div>
  );
}

export default InputField;
