import React, { useState } from "react";
import InputField from '../components/InputField';
import ToggleButton from "../components/ToggleButton";

export default function IndexPage() {
  return (
    <div className="h-screen w-screen bg-white flex items-center justify-center">
      <div className="w-80 space-y-5">
        <label className="flex justify-center text-3xl font-semibold">
          Sign Up
        </label>
        <InputField type="text" required={true} placeholder="Enter your email address" label="Email" message="This email is valid."/>
        <InputField type="text" required={true} placeholder="Enter a username" label="Username" message="This username is available."/>
        <InputField type="password" required={true} placeholder="Enter a password" label="Password" message="This password is weak."/>
      </div>
      <div className="flex items-center justify-between">
        <input type="checkbox"/>
        <a href="forgot-password">Forgot Password?</a>
      </div>
    </div>
  );
}
