import React, { useState, useEffect } from "react";

function ToggleButton() {
  const [toggle, setToggle] = useState(false);

  const onToggle = () => {
    setToggle(!toggle);
  }

  return (
    <div class="h-screen w-screen bg-white flex items-center justify-center">
      <button
        onClick={onToggle}
        class={!toggle ? "relative flex items-center h-8 w-16 p-1 rounded-full bg-gray-200 focus:outline-none" : "relative flex items-center h-9 w-16 p-1 rounded-full bg-blue-500 focus:outline-none"}
      >
        <div class={!toggle ? "h-7 w-7 rounded-full bg-white shadow-md" : "absolute h-7 w-7 rounded-full bg-white right-1 shadow-md" }/>
      </button>
    </div>
  );
}

export default ToggleButton;
