import React, { useState, useEffect } from "react";

function ToggleButton() {
  const [toggle, setToggle] = useState(false);

  const onToggle = () => {
    setToggle(!toggle);
  };

  return (
    <div class="h-screen w-screen bg-white flex items-center justify-center">
      <button
        onClick={onToggle}
        class={`flex items-center h-9 w-16 p-1 rounded-full duration-300 ease-in-out focus:outline-none ${toggle ? "bg-gray-200" : "bg-blue-500"}`}
      >
        <div class={`h-7 w-7 rounded-full bg-white shadow-md duration-300 ease-in-out transform ${toggle ? "translate-x-6" : "-translate-x-6"}`} />
      </button>
    </div>
  );
}

export default ToggleButton;
