import React, { useState, useEffect } from "react";

function ToggleButton() {
  const [toggle, setToggle] = useState(false);

  const onToggle = () => {
    setToggle(!toggle);
  };

  return (
    <div class="h-screen w-screen bg-white flex items-center justify-center">
      <a
        href="/"
        onClick={onToggle}
        class={`flex items-center h-9 w-16 p-1 rounded-full duration-300 ease-in-out cursor-pointer ${toggle ? "bg-gray-200" : "bg-green-500"}`}
      >
        <div class={`h-7 w-7 rounded-full bg-white shadow-md duration-300 ease-in-out transform ${toggle ? "" : "translate-x-7"}`} />
      </a>
    </div>
  );
}

export default ToggleButton;
